transactions:
CREATE TABLE IF NOT EXISTS transactions
(
 transaction_id    int GENERATED ALWAYS AS IDENTITY,
 ledger_id_debit   int NOT NULL,
 ledger_id_credit  int NOT NULL,
 contract_number   int NULL,
 transaction_type  char(50) NOT NULL,
 transaction_sum   bigint NOT NULL,
 debit_account     bigint,
 credit_account    bigint,
 currency_id       int NOT NULL,
 currency          char(3) NOT NULL,
 date              date NOT NULL,
 log_id            int NOT NULL, 
 CONSTRAINT pk_transactions PRIMARY KEY ( transaction_id ),
 CONSTRAINT fk_general_ledger_dt FOREIGN KEY ( ledger_id_debit ) REFERENCES general_ledger ( ledger_id ),
 CONSTRAINT fk_general_ledger_cr FOREIGN KEY ( ledger_id_credit ) REFERENCES general_ledger ( ledger_id ),
 CONSTRAINT fk_contracts FOREIGN KEY (contract_number) REFERENCES contracts ( contract_number ),
 CONSTRAINT fk_accounts_db FOREIGN KEY (debit_account) REFERENCES accounts (account_id),
 CONSTRAINT fk_accounts_cr FOREIGN KEY ( credit_account ) REFERENCES accounts (account_id),
 CONSTRAINT fk_log_id FOREIGN KEY (log_id) REFERENCES transaction_log (log_id),
 CONSTRAINT fk_currency FOREIGN KEY ( currency_id ) REFERENCES exchange_rates ( currency_id )
);

